const setConfigOutput = $('Set Config').first().json;
const data = $input.first().json;

if (!data.products || !data.products[0]) {
  return [{ json: { error: 'No product found', ...setConfigOutput } }];
}

const p = data.products[0];

// Helper functions - return NUMBERS not strings
const getPrice = (csv, i) => {
  if (!csv?.[i]?.length || csv[i].length < 2) return null;
  const val = csv[i][csv[i].length-1];
  return val > 0 ? val / 100 : null;
};

const getPriceHistory = (csv, i, days = 30) => {
  if (!csv?.[i]?.length) return null;
  const now = Date.now();
  const cutoff = now - (days * 24 * 60 * 60 * 1000);
  const history = [];
  for (let j = 0; j < csv[i].length; j += 2) {
    const timestamp = (csv[i][j] + 21564000) * 60000;
    if (timestamp >= cutoff && csv[i][j+1] > 0) {
      history.push({ date: new Date(timestamp).toISOString().split('T')[0], value: csv[i][j+1] / 100 });
    }
  }
  return history.length > 0 ? history : null;
};

const getBSR = (sr) => {
  if (!sr) return null;
  const categories = Object.keys(sr);
  if (!categories.length) return null;
  const mainCat = categories[0];
  const ranks = sr[mainCat];
  return ranks?.length ? ranks[ranks.length - 1] : null;
};

const getAllBSR = (sr) => {
  if (!sr) return {};
  const result = {};
  for (const [catId, ranks] of Object.entries(sr)) {
    if (ranks?.length) {
      result[catId] = {
        current: ranks[ranks.length - 1],
        history: ranks.slice(-10)
      };
    }
  }
  return result;
};

const calcBSRChange = (sr, days = 30) => {
  if (!sr) return null;
  const mainCat = Object.keys(sr)[0];
  if (!mainCat || !sr[mainCat]?.length) return null;
  const ranks = sr[mainCat];
  const current = ranks[ranks.length - 1];
  const older = ranks.length > 2 ? ranks[Math.max(0, ranks.length - (days * 2))] : ranks[0];
  if (!older || !current) return null;
  return current - older;
};

const calcBSRGrowth = (sr, days = 30) => {
  if (!sr) return null;
  const mainCat = Object.keys(sr)[0];
  if (!mainCat || !sr[mainCat]?.length) return null;
  const ranks = sr[mainCat];
  const current = ranks[ranks.length - 1];
  const older = ranks.length > 2 ? ranks[Math.max(0, ranks.length - (days * 2))] : ranks[0];
  if (!older || !current) return null;
  return ((older - current) / older * 100);
};

const calcSalesGrowth = (history) => {
  if (!history || history.length < 2) return null;
  const current = history[history.length - 1];
  const previous = history[Math.max(0, history.length - 2)];
  if (!previous || previous === 0) return null;
  return ((current - previous) / previous * 100);
};

const calcNewRatings = (csv, days = 30) => {
  if (!csv?.[17]?.length || csv[17].length < 4) return null;
  const current = csv[17][csv[17].length - 1];
  const older = csv[17][Math.max(0, csv[17].length - (days * 2) - 1)];
  if (older === undefined) return null;
  return current - older;
};

const mmToInches = (mm) => mm && mm > 0 ? mm / 25.4 : null;
const mmToCm = (mm) => mm && mm > 0 ? mm / 10 : null;
const gramsToOz = (g) => g && g > 0 ? g / 28.3495 : null;
const gramsToLbs = (g) => g && g > 0 ? g / 453.592 : null;
const gramsToKg = (g) => g && g > 0 ? g / 1000 : null;
const keepaTimeToDate = (kt) => kt ? new Date((kt + 21564000) * 60000).toISOString().split('T')[0] : null;

const getSubCategories = (sr, rootCat) => {
  if (!sr) return [];
  const subCats = [];
  for (const [catId, ranks] of Object.entries(sr)) {
    if (catId !== String(rootCat) && ranks?.length) {
      subCats.push({
        categoryId: parseInt(catId),
        currentBSR: ranks[ranks.length - 1],
        bsrHistory: ranks.slice(-10)
      });
    }
  }
  return subCats;
};

const getFulfillmentType = (offers) => {
  if (!offers || !offers.length) return null;
  const fbaCount = offers.filter(o => o.isFBA).length;
  const fbmCount = offers.length - fbaCount;
  if (fbaCount > fbmCount) return 'FBA';
  if (fbmCount > fbaCount) return 'FBM';
  return 'Mixed';
};

const getSizeTierLimits = (tier) => {
  const limits = {
    'Small Standard': { maxWeight: 12, maxLength: 15, maxWidth: 12, maxHeight: 0.75 },
    'Large Standard': { maxWeight: 320, maxLength: 18, maxWidth: 14, maxHeight: 8 },
    'Small Oversize': { maxWeight: 1120, maxLengthPlusGirth: 130, maxLength: 60 },
    'Medium Oversize': { maxWeight: 2400, maxLengthPlusGirth: 130, maxLength: 60 },
    'Large Oversize': { maxWeight: 4480, maxLengthPlusGirth: 165, maxLength: 108 },
    'Special Oversize': { maxWeight: null, maxLengthPlusGirth: null, maxLength: null }
  };
  return limits[tier] || null;
};

const product = {
  asin: p.asin,
  parentAsin: p.parentAsin || null,
  variationCSV: p.variationCSV || null,
  upc: p.upcList?.[0] || null,
  ean: p.eanList?.[0] || null,
  mpn: p.mpn || null,
  partNumber: p.partNumber || null,
  model: p.model || null,
  binding: p.binding || null,
  productGroup: p.productGroup || null,
  productType: p.type || null,
  color: p.color || null,
  size: p.size || null,
  format: p.format || null,
  edition: p.edition || null,
  platform: p.platform || null,
  url: `https://amazon.com/dp/${p.asin}`,
  urlSlug: p.urlSlug || null,
  imageUrl: p.imagesCSV ? `https://images-na.ssl-images-amazon.com/images/I/${p.imagesCSV.split(',')[0]}` : null,
  allImages: p.imagesCSV ? p.imagesCSV.split(',').map(img => `https://images-na.ssl-images-amazon.com/images/I/${img}`) : [],
  imageCount: p.imagesCSV ? p.imagesCSV.split(',').length : 0,
  title: p.title || null,
  description: p.description || null,
  features: p.features || [],
  featureCount: p.features?.length || 0,
  brand: p.brand || null,
  brandStoreName: p.brandStoreName || null,
  brandStoreUrl: p.brandStoreUrl ? `https://amazon.com${p.brandStoreUrl}` : null,
  manufacturer: p.manufacturer || null,
  label: p.label || null,
  publisher: p.publisher || null,
  studio: p.studio || null,
  author: p.author || null,
  contributors: p.contributors || null,
  rootCategory: p.rootCategory || null,
  categoryTree: p.categoryTree || null,
  categories: p.categories || null,
  subCategories: getSubCategories(p.salesRanks, p.rootCategory),
  salesRankReference: p.salesRankReference || null,
  currentBSR: getBSR(p.salesRanks),
  allCategoryBSR: getAllBSR(p.salesRanks),
  bsrChange30d: calcBSRChange(p.salesRanks, 30),
  bsrChange90d: calcBSRChange(p.salesRanks, 90),
  bsrGrowth30d: calcBSRGrowth(p.salesRanks, 30),
  bsrGrowth90d: calcBSRGrowth(p.salesRanks, 90),
  amazonPrice: getPrice(p.csv, 0),
  newPrice: getPrice(p.csv, 1),
  usedPrice: getPrice(p.csv, 2),
  listPrice: getPrice(p.csv, 4),
  collectiblePrice: getPrice(p.csv, 5),
  refurbishedPrice: getPrice(p.csv, 6),
  newFBMShipping: getPrice(p.csv, 7),
  fbaPrice: getPrice(p.csv, 10),
  buyBoxPrice: getPrice(p.csv, 18),
  warehousePrice: getPrice(p.csv, 9),
  ebayNewPrice: getPrice(p.csv, 12),
  ebayUsedPrice: getPrice(p.csv, 13),
  priceHistory30d: getPriceHistory(p.csv, 0, 30),
  priceHistory90d: getPriceHistory(p.csv, 0, 90),
  coupon: p.coupon || null,
  couponHistory: p.couponHistory || null,
  promotions: p.promotions || null,
  monthlySold: p.monthlySold || null,
  monthlySoldHistory: p.monthlySoldHistory || null,
  monthlySalesGrowth: calcSalesGrowth(p.monthlySoldHistory),
  estimatedRevenue: p.monthlySold && getPrice(p.csv, 0) ? p.monthlySold * getPrice(p.csv, 0) : null,
  rating: p.csv?.[16]?.length ? p.csv[16][p.csv[16].length-1] / 10 : null,
  reviewCount: p.csv?.[17]?.length ? p.csv[17][p.csv[17].length-1] : null,
  newlyRatings30d: calcNewRatings(p.csv, 30),
  newlyRatings90d: calcNewRatings(p.csv, 90),
  ratingHistory: p.csv?.[16]?.length ? p.csv[16].slice(-20).filter((_, i) => i % 2 === 1).map(v => v/10) : [],
  reviewCountHistory: p.csv?.[17]?.length ? p.csv[17].slice(-20).filter((_, i) => i % 2 === 1) : [],
  amazonOfferCount: p.csv?.[20]?.length ? p.csv[20][p.csv[20].length-1] : null,
  newOfferCount: p.csv?.[11]?.length ? p.csv[11][p.csv[11].length-1] : null,
  usedOfferCount: p.csv?.[14]?.length ? p.csv[14][p.csv[14].length-1] : null,
  collectibleOfferCount: p.csv?.[15]?.length ? p.csv[15][p.csv[15].length-1] : null,
  refurbishedOfferCount: p.csv?.[19]?.length ? p.csv[19][p.csv[19].length-1] : null,
  variationCount: p.variations?.length || 0,
  variations: (p.variations || []).map(v => ({ asin: v.asin, attributes: v.attributes, dimension: v.dimension })),
  variationAttributes: p.variationAttributes || null,
  packageWeightGrams: p.packageWeight > 0 ? p.packageWeight : null,
  packageHeightMM: p.packageHeight > 0 ? p.packageHeight : null,
  packageLengthMM: p.packageLength > 0 ? p.packageLength : null,
  packageWidthMM: p.packageWidth > 0 ? p.packageWidth : null,
  packageWeightKg: gramsToKg(p.packageWeight),
  packageWeightLbs: gramsToLbs(p.packageWeight),
  packageWeightOz: gramsToOz(p.packageWeight),
  packageHeightCm: mmToCm(p.packageHeight),
  packageHeightIn: mmToInches(p.packageHeight),
  packageLengthCm: mmToCm(p.packageLength),
  packageLengthIn: mmToInches(p.packageLength),
  packageWidthCm: mmToCm(p.packageWidth),
  packageWidthIn: mmToInches(p.packageWidth),
  itemWeightGrams: p.itemWeight > 0 ? p.itemWeight : null,
  itemHeightMM: p.itemHeight > 0 ? p.itemHeight : null,
  itemLengthMM: p.itemLength > 0 ? p.itemLength : null,
  itemWidthMM: p.itemWidth > 0 ? p.itemWidth : null,
  itemWeightKg: gramsToKg(p.itemWeight),
  itemWeightLbs: gramsToLbs(p.itemWeight),
  itemWeightOz: gramsToOz(p.itemWeight),
  itemHeightCm: mmToCm(p.itemHeight),
  itemHeightIn: mmToInches(p.itemHeight),
  itemLengthCm: mmToCm(p.itemLength),
  itemLengthIn: mmToInches(p.itemLength),
  itemWidthCm: mmToCm(p.itemWidth),
  itemWidthIn: mmToInches(p.itemWidth),
  dimWeightOz: null,
  dimWeightLbs: null,
  numberOfItems: p.numberOfItems > 0 ? p.numberOfItems : null,
  packageQuantity: p.packageQuantity > 0 ? p.packageQuantity : null,
  fbaFees: p.fbaFees ? { pickAndPackFee: p.fbaFees.pickAndPackFee ? p.fbaFees.pickAndPackFee / 100 : null, perItemFee: p.fbaFees.perItemFee ? p.fbaFees.perItemFee / 100 : null, storageFee: p.fbaFees.storageFee ? p.fbaFees.storageFee / 100 : null, lastUpdate: keepaTimeToDate(p.fbaFees.lastUpdate) } : null,
  referralFeePercentage: p.referralFeePercentage || null,
  variableClosingFee: p.variableClosingFee ? p.variableClosingFee / 100 : null,
  listedSince: keepaTimeToDate(p.listedSince),
  trackingSince: keepaTimeToDate(p.trackingSince),
  lastUpdate: keepaTimeToDate(p.lastUpdate),
  lastPriceChange: keepaTimeToDate(p.lastPriceChange),
  lastRatingUpdate: keepaTimeToDate(p.lastRatingUpdate),
  availabilityAmazon: p.availabilityAmazon,
  isAvailable: p.availabilityAmazon === 0 || (p.csv?.[0]?.length > 0 && p.csv[0][p.csv[0].length-1] > 0),
  fulfillmentType: getFulfillmentType(p.offers),
  fbaOfferCount: p.offers ? p.offers.filter(o => o.isFBA).length : 0,
  fbmOfferCount: p.offers ? p.offers.filter(o => !o.isFBA).length : 0,
  isAdultProduct: p.isAdultProduct || false,
  isEligibleForTradeIn: p.isEligibleForTradeIn || false,
  isEligibleForSuperSaverShipping: p.isEligibleForSuperSaverShipping || false,
  isSNS: p.isSNS || false,
  isRedirectASIN: p.isRedirectASIN || false,
  isPrimeExclusive: p.isPrimeExclusive || false,
  launchpad: p.launchpad || false,
  hazardousMaterials: p.hazardousMaterialType || null,
  hasAPlus: !!p.aPlus,
  aPlusModules: p.aPlus || null,
  hasVideos: p.videos?.length > 0,
  videos: p.videos || [],
  videoCount: p.videos?.length || 0,
  frequentlyBoughtTogether: p.frequentlyBoughtTogether || null,
  buyBoxSellerId: p.stats?.buyBoxSellerId || null,
  buyBoxIsFBA: p.stats?.buyBoxIsFBA || null,
  buyBoxIsAmazon: p.stats?.buyBoxIsAmazon || null,
  buyBoxPrice90dAvg: p.stats?.buyBoxPrice ? p.stats.buyBoxPrice / 100 : null,
  offers: (p.offers || []).slice(0, 10).map(o => ({ sellerId: o.sellerId, condition: o.condition, isPrime: o.isPrime, isFBA: o.isFBA, isShippable: o.isShippable, rating: o.rating, ratingCount: o.ratingCount, shipsFrom: o.shipsFromCountry })),
  offerCount: p.offers?.length || 0,
  ingredients: p.ingredients || null,
  activeIngredients: p.activeIngredients || null,
  specialIngredients: p.specialIngredients || null,
  nutritionFacts: p.nutritionFacts || null,
  warnings: p.warnings || null,
  directions: p.directions || null,
  stats: p.stats ? { current: p.stats.current, avg30: p.stats.avg30, avg90: p.stats.avg90, outOfStockPercentage30: p.stats.outOfStockPercentage30, outOfStockPercentage90: p.stats.outOfStockPercentage90, totalOfferCount: p.stats.totalOfferCount, lightningDealInfo: p.stats.lightningDealInfo, buyBoxStats: p.stats.buyBoxStats } : null,
  _raw: { domainId: p.domainId, productType: p.productType, hasReviews: p.hasReviews, tokensConsumed: data.tokensConsumed, timestamp: data.timestamp }
};

if (product.packageHeightIn && product.packageLengthIn && product.packageWidthIn) {
  const dimWeight = (product.packageHeightIn * product.packageLengthIn * product.packageWidthIn) / 139;
  product.dimWeightOz = dimWeight;
  product.dimWeightLbs = dimWeight / 16;
}

product.daysListed = product.listedSince ? Math.floor((Date.now() - new Date(product.listedSince).getTime()) / (1000 * 60 * 60 * 24)) : null;
product.reviewVelocity = product.reviewCount && product.daysListed > 0 ? product.reviewCount / product.daysListed : null;
product.revenuePerReview = product.estimatedRevenue && product.reviewCount > 0 ? product.estimatedRevenue / product.reviewCount : null;

if (product.packageWeightOz && product.packageLengthIn && product.packageWidthIn && product.packageHeightIn) {
  const weight = product.packageWeightOz;
  const length = product.packageLengthIn;
  const width = product.packageWidthIn;
  const height = product.packageHeightIn;
  const girth = 2 * (width + height);
  const lengthPlusGirth = length + girth;
  if (weight <= 12 && length <= 15 && width <= 12 && height <= 0.75) {
    product.sizeTier = 'Small Standard';
  } else if (weight <= 320 && length <= 18 && width <= 14 && height <= 8) {
    product.sizeTier = 'Large Standard';
  } else if (weight <= 1120 && lengthPlusGirth <= 130 && length <= 60) {
    product.sizeTier = 'Small Oversize';
  } else if (weight <= 2400 && lengthPlusGirth <= 130 && length <= 60) {
    product.sizeTier = 'Medium Oversize';
  } else if (weight <= 4480 && lengthPlusGirth <= 165 && length <= 108) {
    product.sizeTier = 'Large Oversize';
  } else {
    product.sizeTier = 'Special Oversize';
  }
  product.sizeTierLimits = getSizeTierLimits(product.sizeTier);
  if (product.sizeTierLimits?.maxWeight) {
    product.remainingWeightOz = product.sizeTierLimits.maxWeight - weight;
    product.remainingWeightG = product.remainingWeightOz * 28.3495;
  }
} else {
  product.sizeTier = null;
  product.sizeTierLimits = null;
}

// Spread setConfigOutput to flatten config structure
return [{ json: { product, ...setConfigOutput } }];

const prev = $('Transform Product').first().json;
const data = $input.first().json;
const kws = data.tasks?.[0]?.result?.map(r => ({ keyword: r.keyword, searchVolume: r.search_volume, competition: r.competition, cpc: r.cpc })) || [];
return [{ json: { ...prev, keywords: kws } }];

// Prepare data for Supabase tables with all analysis fields
const items = $input.all();
const results = [];

// Helper to safely parse JSON strings or return array
function ensureArray(val) {
  if (Array.isArray(val)) return val;
  if (typeof val === 'string') {
    try { return JSON.parse(val); } catch { return []; }
  }
  return [];
}

for (const item of items) {
  const data = item.json;
  const product = data.product || {};
  const keywords = ensureArray(data.keywords);
  const reviews = data.reviews?.items || ensureArray(data.reviews) || [];
  const reviewSummary = data.review_summary || {};
  const marketAnalysis = data.marketAnalysis || data.market_analysis || {};
  const reviewAnalysis = data.reviewAnalysis || data.review_analysis || {};
  const fbaAnalysis = data.fba_analysis || {};
  const serpAnalysis = data.serp_analysis || {};
  const variationAnalysis = data.variation_analysis || {};
  const socialMedia = data.socialMedia || {};
  const qaAnalysis = data.qaAnalysis || {};
  const adAnalysis = data.adAnalysis || {};
  const competitorAnalysis = data.competitorAnalysis || {};
  const questions = ensureArray(data.questions);
  const config = data.config || {};

  const researchId = `${product.asin || data.asin}_${Date.now()}`;

  // Product record with all analysis fields
  const productRecord = {
    research_id: researchId,
    asin: product.asin || data.asin,
    parent_asin: product.parentAsin,
    upc: product.upc,
    ean: product.ean,
    title: product.title,
    description: product.description,
    features: product.features,
    brand: product.brand,
    manufacturer: product.manufacturer,
    root_category: product.rootCategory,
    category_tree: product.categoryTree,
    current_bsr: product.currentBSR,
    sales_rank_reference: product.salesRankReference,
    amazon_price: product.amazonPrice,
    new_price: product.newPrice,
    buy_box_price: product.buyBoxPrice,
    monthly_sold: product.monthlySold,
    rating: product.rating,
    review_count: product.reviewCount,
    variation_count: product.variationCount,
    package_weight_grams: product.packageWeightGrams,
    package_height_mm: product.packageHeightMM,
    package_length_mm: product.packageLengthMM,
    package_width_mm: product.packageWidthMM,
    fba_fees: fbaAnalysis,
    size_tier: product.sizeTier || fbaAnalysis.size_tier,
    dim_weight_oz: product.dimWeightOz,
    remaining_weight_oz: product.remainingWeightOz,
    remaining_weight_g: product.remainingWeightG,
    serp_analysis: serpAnalysis,
    serp_position: serpAnalysis.product_position,
    serp_on_page_one: serpAnalysis.on_page_one,
    serp_p1_competitors: serpAnalysis.p1_competitor_count,
    serp_p1_avg_rating: serpAnalysis.p1_avg_rating,
    serp_p1_avg_reviews: serpAnalysis.p1_avg_reviews,
    variation_analysis: variationAnalysis,
    var_sum_sales: variationAnalysis.sum_monthly_sales,
    var_sum_revenue: variationAnalysis.sum_monthly_revenue,
    var_median_price: variationAnalysis.median_price,
    var_median_rating: variationAnalysis.median_rating,
    social_media_analysis: socialMedia.skipped ? null : socialMedia,
    sm_instagram_handle: socialMedia.instagram?.handle,
    sm_instagram_followers: socialMedia.instagram?.followers,
    sm_instagram_following: socialMedia.instagram?.following,
    sm_instagram_posts: socialMedia.instagram?.posts,
    sm_instagram_bio: socialMedia.instagram?.bio,
    sm_tiktok_handle: socialMedia.tiktok?.handle,
    sm_tiktok_followers: socialMedia.tiktok?.followers,
    sm_tiktok_likes: socialMedia.tiktok?.likes,
    sm_tiktok_videos: socialMedia.tiktok?.videos,
    sm_youtube_channel: socialMedia.youtube?.channel,
    sm_youtube_subscribers: socialMedia.youtube?.subscribers,
    sm_youtube_views: socialMedia.youtube?.views,
    sm_total_followers: socialMedia.totalFollowers,
    sm_primary_platform: socialMedia.primaryPlatform,
    qa_analysis: qaAnalysis.skipped ? null : qaAnalysis,
    qa_total_questions: qaAnalysis.totalQuestions,
    qa_answered_questions: qaAnalysis.answeredQuestions,
    qa_unanswered_questions: qaAnalysis.unansweredQuestions,
    qa_avg_answers_per_question: qaAnalysis.avgAnswersPerQuestion,
    qa_top_questions: qaAnalysis.topQuestions,
    qa_common_themes: qaAnalysis.commonThemes,
    ad_analysis: adAnalysis.skipped ? null : adAnalysis,
    ad_facebook_active_ads: adAnalysis.facebook?.activeAds,
    ad_facebook_total_ads: adAnalysis.facebook?.totalAds,
    ad_facebook_ad_types: adAnalysis.facebook?.adTypes,
    ad_google_active_ads: adAnalysis.google?.advertisersFound,
    ad_competitor_ad_spend_estimate: adAnalysis.spendEstimate,
    ad_top_ad_creatives: adAnalysis.facebook?.topCreatives,
    market_analysis: marketAnalysis,
    review_analysis: reviewAnalysis,
    ai_opportunity_score: marketAnalysis.market_opportunity_score,
    ai_risk_profile: marketAnalysis.risk_profile,
    ai_recession_proof_score: marketAnalysis.recession_proof_score,
    ai_competitive_intensity: marketAnalysis.competitive_intensity,
    ai_is_consumable: marketAnalysis.is_consumable,
    ai_sentiment: reviewAnalysis.overall_sentiment,
    ai_sentiment_score: reviewAnalysis.sentiment_score,
    listed_since: product.listedSince,
    url: product.url,
    image_url: product.imageUrl
  };

  // Keyword records
  const kwRecs = keywords.map(kw => ({
    research_id: researchId,
    asin: product.asin || data.asin,
    keyword: kw.keyword,
    search_volume: kw.search_volume,
    competition: kw.competition,
    cpc: kw.cpc
  }));

  // Review records
  const revRecs = reviews.map((rev, idx) => ({
    research_id: researchId,
    asin: product.asin || data.asin,
    review_id: rev.id || `rev_${idx}`,
    rating: rev.rating,
    title: rev.title,
    body: rev.text || rev.body,
    helpful_votes: rev.helpful_votes || 0,
    verified_purchase: rev.verified_purchase,
    review_date: rev.date,
    has_images: rev.has_images || false,
    has_videos: rev.has_videos || false
  }));

  // Q&A records
  const qaRecs = questions.map((q, idx) => ({
    research_id: researchId,
    asin: product.asin || data.asin,
    question_id: q.id || `qa_${idx}`,
    question: q.question || q.text,
    answer: q.answer || q.answers?.[0]?.text,
    answer_count: q.answers?.length || (q.answer ? 1 : 0),
    votes: q.votes || q.helpful_votes || 0,
    asked_by: q.asked_by || q.author,
    answered_by: q.answered_by || q.answers?.[0]?.author,
    is_seller_answer: q.is_seller_answer || q.answers?.[0]?.is_seller || false
  }));

  // Review summary record
  const summaryRec = reviews.length > 0 ? {
    research_id: researchId,
    asin: product.asin || data.asin,
    total_reviews: reviews.length,
    avg_rating: reviewSummary.avg_rating || (reviews.reduce((s, r) => s + (r.rating || 0), 0) / reviews.length),
    ai_sentiment: reviewAnalysis.overall_sentiment,
    ai_pain_points: reviewAnalysis.pain_points,
    ai_praised_features: reviewAnalysis.praised_features,
    ai_key_insight: reviewAnalysis.key_insight
  } : null;

  // Social media profile records
  const socialRecs = [];
  if (socialMedia && !socialMedia.skipped) {
    if (socialMedia.instagram?.handle) {
      socialRecs.push({
        research_id: researchId,
        asin: product.asin || data.asin,
        brand: product.brand,
        platform: 'instagram',
        handle: socialMedia.instagram.handle,
        followers: socialMedia.instagram.followers,
        following: socialMedia.instagram.following,
        posts_count: socialMedia.instagram.posts,
        bio: socialMedia.instagram.bio,
        website: socialMedia.instagram.website,
        verified: socialMedia.instagram.verified,
        profile_image: socialMedia.instagram.profileImage
      });
    }
    if (socialMedia.tiktok?.handle) {
      socialRecs.push({
        research_id: researchId,
        asin: product.asin || data.asin,
        brand: product.brand,
        platform: 'tiktok',
        handle: socialMedia.tiktok.handle,
        followers: socialMedia.tiktok.followers,
        following: socialMedia.tiktok.following,
        total_likes: socialMedia.tiktok.likes,
        posts_count: socialMedia.tiktok.videos,
        bio: socialMedia.tiktok.bio,
        verified: socialMedia.tiktok.verified
      });
    }
    if (socialMedia.youtube?.channel) {
      socialRecs.push({
        research_id: researchId,
        asin: product.asin || data.asin,
        brand: product.brand,
        platform: 'youtube',
        handle: socialMedia.youtube.channel,
        followers: socialMedia.youtube.subscribers,
        posts_count: socialMedia.youtube.videos,
        bio: socialMedia.youtube.description
      });
    }
  }

  // Competitor records
  const competitorRecs = [];
  if (competitorAnalysis && !competitorAnalysis.skipped && competitorAnalysis.competitors) {
    for (const comp of competitorAnalysis.competitors) {
      competitorRecs.push({
        research_id: researchId,
        target_asin: product.asin || data.asin,
        competitor_asin: comp.asin,
        rank: comp.rank,
        final_score: comp.finalScore,
        title: comp.title,
        brand: comp.brand,
        price: comp.pricing?.currentPrice,
        rating: comp.performance?.rating,
        review_count: comp.performance?.reviewCount,
        bsr: comp.performance?.bsr,
        sales_estimate: comp.performance?.salesEstimate,
        keyword_intersections: comp.keywords?.intersections,
        avg_position: comp.keywords?.avgPosition,
        ranked_keywords: comp.keywords?.rankedKeywordCount,
        scores: comp.scores,
        top_keywords: comp.keywords?.topKeywords
      });
    }
  }

  results.push({
    json: {
      config,
      researchId,
      productRecord,
      kwRecs,
      revRecs,
      qaRecs,
      socialRecs,
      competitorRecs,
      summaryRec,
      sbUrl: config.sbUrl,
      sbKey: config.sbKey,
      counts: {
        keywords: kwRecs.length,
        reviews: revRecs.length,
        questions: qaRecs.length,
        socialProfiles: socialRecs.length,
        competitors: competitorRecs.length,
        hasSummary: !!summaryRec
      }
    }
  });
}

return results;

const prep = $('Prepare Supabase Data').first().json;
return [{ json: {
  success: true,
  researchId: prep.rid,
  asin: prep.original.product?.asin,
