parameters.jsCode:
// Parse OpenAI market analysis response
const items = $input.all();
const results = [];

// Get the original data from Merge Reviews node
const mergeReviewsData = $('Merge Reviews').first().json;
const config = mergeReviewsData.config;
const product = mergeReviewsData.product;
const keywords = mergeReviewsData.keywords;
const reviews = mergeReviewsData.reviews;

for (const item of items) {
  const data = item.json;
  let marketAnalysis = null;

  try {
    // OpenAI response structure: data.choices[0].message.content
    const responseText = data.choices?.[0]?.message?.content || '';
    const jsonMatch = responseText.match(/\{[\s\S]*\}/);

    if (jsonMatch) {
      marketAnalysis = JSON.parse(jsonMatch[0]);
    }
  } catch (e) {
    marketAnalysis = { error: 'Failed to parse AI response', raw: data.choices?.[0]?.message?.content?.substring(0, 500) };
  }

  results.push({
    json: {
      config,
      product,
      keywords,
      reviews,
      marketAnalysis
    }
  });
}

return results;

// Parse OpenAI review analysis response
const items = $input.all();
const results = [];

// Get the original data from Has Reviews? node which has marketAnalysis
const hasReviewsData = $('Has Reviews?').first().json;
const config = hasReviewsData.config;
const product = hasReviewsData.product;
const keywords = hasReviewsData.keywords;
const reviews = hasReviewsData.reviews;
const marketAnalysis = hasReviewsData.marketAnalysis;

for (const item of items) {
  const data = item.json;
  let reviewAnalysis = null;

  try {
    const text = data.choices?.[0]?.message?.content || '';
    const match = text.match(/\{[\s\S]*\}/);
    if (match) reviewAnalysis = JSON.parse(match[0]);
  } catch (e) {
    reviewAnalysis = { error: 'Parse failed' };
  }

  results.push({
    json: {
      config,
      product,
      keywords,
      reviews,
      marketAnalysis,
      reviewAnalysis
    }
  });
}

return results;

// Normalize reviews from Oxylabs, Bright Data or Apify to common format
const mergeKwData = $('Merge KW').first().json;
const config = mergeKwData.config;
const product = mergeKwData.product;
const keywords = mergeKwData.keywords;

const apiResponse = $input.all().map(i => i.json);
let reviews = [];
let source = 'unknown';

// Check if this is Oxylabs response (has results array with content)
const firstItem = apiResponse[0];
if (firstItem && firstItem.results && firstItem.results[0] && firstItem.results[0].content) {
  source = 'oxylabs';
  const content = firstItem.results[0].content;
  const rawReviews = content.reviews || [];

  reviews = rawReviews.map(r => ({
    id: r.id || r.review_id || '',
    title: r.title || r.review_title || '',
    text: r.content || r.text || r.body || '',
    rating: r.rating || r.stars || 0,
    date: r.date || r.timestamp || '',
    verified: r.verified_purchase || r.verified || false,
    helpful: r.helpful_votes || r.helpful_count || 0,
    author: r.author || r.author_name || r.profile_name || '',
    location: r.location || r.author_location || ''
  }));
} else {
  let flatResponse = apiResponse.flat();

  // Detect Bright Data format (review_text field)
  if (flatResponse.length > 0 && flatResponse[0] && flatResponse[0].review_text) {
    source = 'brightdata';
    reviews = flatResponse.map(r => ({
      id: r.review_id || '',
      title: r.review_title || '',
      text: r.review_text || '',
      rating: r.rating || 0,
      date: r.review_date || '',
      verified: r.verified_purchase || false,
      helpful: r.helpful_count || 0,
      author: r.reviewer_name || ''
    }));
  } 
  // Detect Apify/junglee format (reviewTitle field)
  else if (flatResponse.length > 0 && flatResponse[0] && flatResponse[0].reviewTitle) {
    source = 'apify';
    reviews = flatResponse.map(r => ({
      id: r.reviewId || '',
      title: r.reviewTitle || '',
      text: r.reviewDescription || '',
      rating: r.ratingScore || 0,
      date: r.date || '',
      verified: r.isVerified || false,
      helpful: parseInt(r.reviewReaction) || 0,
      author: r.userId || ''
    }));
  }
}

return [{
  json: {
    config,
    product,
    keywords,
    reviews: {
      source,
      count: reviews.length,
      items: reviews
    }
  }
}];

// FBA Calculator - Calculate dimensional weight, size tier, and fees
// Uses Keepa dimension data from Transform Product

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const product = data.product || data;

  // Extract dimensions (Keepa returns in mm for dimensions, grams for weight)
  const pkgHeightMm = product.package_height_mm || product.packageHeight || 0;
  const pkgLengthMm = product.package_length_mm || product.packageLength || 0;
  const pkgWidthMm = product.package_width_mm || product.packageWidth || 0;
  const pkgWeightG = product.package_weight_grams || product.packageWeight || 0;

  // Convert to inches and pounds
  const pkgHeightIn = pkgHeightMm / 25.4;
  const pkgLengthIn = pkgLengthMm / 25.4;
  const pkgWidthIn = pkgWidthMm / 25.4;
  const pkgWeightLbs = pkgWeightG / 453.592;
  const pkgWeightOz = pkgWeightG / 28.3495;

  // Calculate dimensional weight (L x W x H / 139 for standard, /166 for oversized)
  const cubicInches = pkgLengthIn * pkgWidthIn * pkgHeightIn;
  const dimWeightLbs = cubicInches / 139;
  const dimWeightOz = dimWeightLbs * 16;

  // Determine billable weight (greater of actual vs dimensional)
  const billableWeightOz = Math.max(pkgWeightOz, dimWeightOz);
  const billableWeightLbs = billableWeightOz / 16;

  // Sort dimensions for size tier calculation
  const dims = [pkgLengthIn, pkgWidthIn, pkgHeightIn].sort((a, b) => b - a);
  const longest = dims[0];
  const median = dims[1];
  const shortest = dims[2];
  const girth = 2 * (median + shortest);
  const lengthPlusGirth = longest + girth;

  // Determine FBA Size Tier (2024 standards)
  let sizeTier = '';
  let fulfillmentFee = 0;
  let weightLimit = 0;

  if (longest <= 15 && median <= 12 && shortest <= 0.75 && billableWeightOz <= 16) {
    sizeTier = 'Small Standard';
    weightLimit = 16;
    if (billableWeightOz <= 4) fulfillmentFee = 3.22;
    else if (billableWeightOz <= 8) fulfillmentFee = 3.40;
    else if (billableWeightOz <= 12) fulfillmentFee = 3.58;
    else fulfillmentFee = 3.77;
  } else if (longest <= 18 && median <= 14 && shortest <= 8 && billableWeightLbs <= 20) {
    sizeTier = 'Large Standard';
    weightLimit = 320; // 20 lbs in oz
    if (billableWeightOz <= 4) fulfillmentFee = 3.86;
    else if (billableWeightOz <= 8) fulfillmentFee = 4.08;
    else if (billableWeightOz <= 12) fulfillmentFee = 4.24;
    else if (billableWeightOz <= 16) fulfillmentFee = 4.75;
    else if (billableWeightLbs <= 1.5) fulfillmentFee = 5.40;
    else if (billableWeightLbs <= 2) fulfillmentFee = 5.69;
    else if (billableWeightLbs <= 2.5) fulfillmentFee = 6.10;
    else if (billableWeightLbs <= 3) fulfillmentFee = 6.39;
    else fulfillmentFee = 6.39 + 0.16 * Math.ceil(billableWeightLbs - 3);
  } else if (longest <= 60 && median <= 30 && lengthPlusGirth <= 130 && billableWeightLbs <= 70) {
    sizeTier = 'Large Bulky';
    weightLimit = 1120; // 70 lbs in oz
    fulfillmentFee = 9.73 + 0.42 * Math.max(0, billableWeightLbs - 1);
  } else {
    sizeTier = 'Extra Large';
    weightLimit = 2400; // 150 lbs in oz
    if (billableWeightLbs <= 50) fulfillmentFee = 26.33 + 0.38 * Math.max(0, billableWeightLbs - 1);
    else if (billableWeightLbs <= 70) fulfillmentFee = 40.12 + 0.75 * Math.max(0, billableWeightLbs - 51);
    else fulfillmentFee = 54.81 + 0.75 * Math.max(0, billableWeightLbs - 71);
  }

  // Calculate remaining capacity in size tier
  const remainingWeightOz = weightLimit - billableWeightOz;
  const remainingWeightG = remainingWeightOz * 28.3495;

  // Peak season surcharge (Oct-Jan)
  const peakSurcharge = sizeTier.includes('Standard') ? 0.35 : 0.50;
  const peakFee = fulfillmentFee + peakSurcharge;

  // Referral fee (typically 15% for most categories)
  const referralFeePercent = product.referral_fee_percentage || 15;
  const price = product.buy_box_price || product.amazon_price || product.new_price || 0;
  const referralFee = (price * referralFeePercent / 100);

  // Total FBA cost
  const totalFbaCost = fulfillmentFee + referralFee;
  const totalFbaCostPeak = peakFee + referralFee;

  results.push({
    json: {
      ...data,
      fba_analysis: {
        // Dimensions in multiple units
        package_length_in: Math.round(pkgLengthIn * 100) / 100,
        package_width_in: Math.round(pkgWidthIn * 100) / 100,
        package_height_in: Math.round(pkgHeightIn * 100) / 100,
        package_weight_oz: Math.round(pkgWeightOz * 100) / 100,
        package_weight_lbs: Math.round(pkgWeightLbs * 100) / 100,

        // Calculated values
        dim_weight_oz: Math.round(dimWeightOz * 100) / 100,
        dim_weight_lbs: Math.round(dimWeightLbs * 100) / 100,
        billable_weight_oz: Math.round(billableWeightOz * 100) / 100,
        billable_weight_lbs: Math.round(billableWeightLbs * 100) / 100,

        // Size tier info
        size_tier: sizeTier,
        weight_limit_oz: weightLimit,
        remaining_weight_oz: Math.round(remainingWeightOz * 100) / 100,
        remaining_weight_g: Math.round(remainingWeightG * 100) / 100,
        capacity_utilization: Math.round((billableWeightOz / weightLimit) * 10000) / 100,

        // Fee breakdown
        fulfillment_fee: Math.round(fulfillmentFee * 100) / 100,
        fulfillment_fee_peak: Math.round(peakFee * 100) / 100,
        referral_fee_percent: referralFeePercent,
        referral_fee: Math.round(referralFee * 100) / 100,
        total_fba_cost: Math.round(totalFbaCost * 100) / 100,
        total_fba_cost_peak: Math.round(totalFbaCostPeak * 100) / 100,

        // Margin analysis
        price: price,
        gross_margin: Math.round((price - totalFbaCost) * 100) / 100,
        gross_margin_percent: price > 0 ? Math.round(((price - totalFbaCost) / price) * 10000) / 100 : 0,
        gross_margin_peak: Math.round((price - totalFbaCostPeak) * 100) / 100
      }
    }
  });
}

return results;

// Process SERP rankings
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const prevData = $('Transform Keywords').item?.json || {};
  const targetAsin = prevData.asin || '';

  const serpResults = data?.tasks?.[0]?.result?.[0]?.items || [];
  const keyword = data?.tasks?.[0]?.data?.keyword || '';

  let productPosition = null;
  let onPageOne = false;
  const competitors = [];

  for (let i = 0; i < serpResults.length; i++) {
    const serpItem = serpResults[i];
    if (serpItem.type === 'amazon_product_info') {
      const itemAsin = serpItem.asin || '';
      const position = i + 1;

      if (itemAsin === targetAsin) {
        productPosition = position;
        onPageOne = position <= 48;
      } else if (position <= 20) {
        competitors.push({
          position, asin: itemAsin,
          title: serpItem.title || '',
          price: serpItem.price?.current || 0,
          rating: serpItem.rating?.value || 0,
          reviews: serpItem.reviews_count || 0
        });
      }
    }
  }

  const p1Comps = competitors.filter(c => c.position <= 48);

  results.push({
    json: {
      ...prevData,
      serp_analysis: {
        search_keyword: keyword,
        product_position: productPosition,
        on_page_one: onPageOne,
        p1_competitor_count: p1Comps.length,
        p1_avg_rating: p1Comps.length ? Math.round(p1Comps.reduce((s,c) => s + c.rating, 0) / p1Comps.length * 100) / 100 : 0,
        p1_avg_reviews: p1Comps.length ? Math.round(p1Comps.reduce((s,c) => s + c.reviews, 0) / p1Comps.length) : 0,
        top_competitors: competitors.slice(0, 10)
      }
    }
  });
}

return results;

// Extract child ASINs from variations for batch request
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const product = data.product || {};
  const variations = product.variations || [];

  // Extract unique child ASINs
  const childAsins = [];
  for (const variation of variations) {
    if (variation.asin && variation.asin !== product.asin) {
      childAsins.push(variation.asin);
    }
  }

  // Limit to 50 variations for cost control
  const asinsToFetch = [...new Set(childAsins)].slice(0, 50);

  results.push({
    json: {
      ...data,
      variation_asins: asinsToFetch,
      variation_asin_string: asinsToFetch.join(',')
    }
  });
}

return results;

// Aggregate variation metrics: MEDIAN, MAX, SUM calculations
const items = $input.all();
const results = [];

// Helper function for median
function median(arr) {
  if (!arr.length) return 0;
  const sorted = [...arr].sort((a, b) => a - b);
  const mid = Math.floor(sorted.length / 2);
  return sorted.length % 2 ? sorted[mid] : (sorted[mid - 1] + sorted[mid]) / 2;
}

for (const item of items) {
  const data = item.json;
  const prevData = $('Extract Variation ASINs').item?.json || data;
  const products = data.products || [];

  // Collect metrics from all variations
  const sales = [];
  const revenues = [];
  const prices = [];
  const ratings = [];
  const reviewCounts = [];
  const listingAges = [];

  const now = Date.now();

  for (const product of products) {
    // Monthly sold
    if (product.monthlySold != null && product.monthlySold >= 0) {
      sales.push(product.monthlySold);
    }

    // Price (convert from Keepa format: cents)
    const price = product.stats?.current?.[0] || product.stats?.current?.[1];
    if (price && price > 0) {
      const priceUsd = price / 100;
      prices.push(priceUsd);

      // Revenue estimate
      if (product.monthlySold) {
        revenues.push(priceUsd * product.monthlySold);
      }
    }

    // Rating (Keepa stores as value * 10)
    const rating = product.stats?.current?.[16];
    if (rating && rating > 0) {
      ratings.push(rating / 10);
    }

    // Review count
    const reviews = product.stats?.current?.[17];
    if (reviews && reviews > 0) {
      reviewCounts.push(reviews);
    }

    // Listing age (days since listed)
    if (product.listedSince) {
      const listedDate = (product.listedSince + 21564000) * 60000; // Keepa time format
      const daysListed = Math.floor((now - listedDate) / (1000 * 60 * 60 * 24));
      if (daysListed > 0) {
        listingAges.push(daysListed);
      }
    }
  }

  // Calculate aggregates
  const variationAnalysis = {
    total_variations_analyzed: products.length,

    // Sales metrics
    sum_monthly_sales: sales.reduce((a, b) => a + b, 0),
    max_monthly_sales: sales.length ? Math.max(...sales) : 0,
    median_monthly_sales: Math.round(median(sales)),

    // Revenue metrics
    sum_monthly_revenue: Math.round(revenues.reduce((a, b) => a + b, 0) * 100) / 100,
    max_monthly_revenue: Math.round((revenues.length ? Math.max(...revenues) : 0) * 100) / 100,
    median_monthly_revenue: Math.round(median(revenues) * 100) / 100,

    // Price metrics
    min_price: Math.round((prices.length ? Math.min(...prices) : 0) * 100) / 100,
    max_price: Math.round((prices.length ? Math.max(...prices) : 0) * 100) / 100,
    median_price: Math.round(median(prices) * 100) / 100,
    price_range: Math.round(((prices.length ? Math.max(...prices) - Math.min(...prices) : 0)) * 100) / 100,

    // Rating metrics
    avg_rating: ratings.length ? Math.round(ratings.reduce((a, b) => a + b, 0) / ratings.length * 100) / 100 : 0,
    median_rating: Math.round(median(ratings) * 100) / 100,

    // Review metrics
    sum_reviews: reviewCounts.reduce((a, b) => a + b, 0),
    max_reviews: reviewCounts.length ? Math.max(...reviewCounts) : 0,
    median_reviews: Math.round(median(reviewCounts)),

    // Listing age metrics
    avg_listing_age_days: listingAges.length ? Math.round(listingAges.reduce((a, b) => a + b, 0) / listingAges.length) : 0,
    median_listing_age_days: Math.round(median(listingAges)),
    oldest_listing_days: listingAges.length ? Math.max(...listingAges) : 0,
    newest_listing_days: listingAges.length ? Math.min(...listingAges) : 0,

    // Best performer (by revenue)
    best_variation: revenues.length ? {
      asin: products[revenues.indexOf(Math.max(...revenues))]?.asin || '',
      monthly_revenue: Math.max(...revenues),
      monthly_sales: sales[revenues.indexOf(Math.max(...revenues))] || 0
    } : null
  };

  results.push({
    json: {
      ...prevData,
      variation_analysis: variationAnalysis
    }
  });
}

return results;

// Aggregate social media data from Scrape Creators API
// Response structures differ:
// - Instagram: { success, data: { user: { edge_followed_by: { count } } } }
// - TikTok: { success, user: { uniqueId }, stats: { followerCount } } - NOT wrapped in data
// - YouTube: { success, channelId, subscriberCount } - NOT wrapped in data

const items = $input.all();
const originalData = $('Run Social Media?').first().json;

let instagram = { handle: null, followers: 0, following: 0, posts: 0, bio: null, website: null, verified: false, profileImage: null };
let tiktok = { handle: null, followers: 0, following: 0, likes: 0, videos: 0, bio: null, verified: false };
let youtube = { channel: null, subscribers: 0, views: 0, videos: 0, description: null };

for (const item of items) {
  const json = item.json || {};

  // Instagram: has data.user.edge_followed_by (Instagram's GraphQL structure wrapped in data)
  if (json.data?.user?.edge_followed_by) {
    const user = json.data.user;
    instagram = {
      handle: user.username || user.full_name || null,
      followers: user.edge_followed_by?.count || 0,
      following: user.edge_follow?.count || 0,
      posts: user.edge_owner_to_timeline_media?.count || 0,
      bio: user.biography || null,
      website: user.external_url || null,
      verified: user.is_verified || false,
      profileImage: user.profile_pic_url_hd || user.profile_pic_url || null
    };
  }
  // TikTok: has stats.followerCount at root level (NOT wrapped in data)
  else if (json.stats?.followerCount !== undefined || json.user?.uniqueId) {
    const user = json.user || {};
    const stats = json.stats || {};
    tiktok = {
      handle: user.uniqueId || user.nickname || null,
      followers: stats.followerCount || 0,
      following: stats.followingCount || 0,
      likes: stats.heartCount || stats.heart || 0,
      videos: stats.videoCount || 0,
      bio: user.signature || null,
      verified: user.verified || false
    };
  }
  // YouTube: has subscriberCount at root level (NOT wrapped in data)
  else if (json.subscriberCount !== undefined || json.channelId) {
    youtube = {
      channel: json.name || json.handle || null,
      subscribers: json.subscriberCount || 0,
      views: json.viewCount || 0,
      videos: json.videoCount || 0,
      description: json.description || null
    };
  }
}

const totalFollowers = instagram.followers + tiktok.followers + youtube.subscribers;
let primaryPlatform = null;
if (instagram.followers >= tiktok.followers && instagram.followers >= youtube.subscribers) {
  primaryPlatform = 'instagram';
} else if (tiktok.followers >= youtube.subscribers) {
  primaryPlatform = 'tiktok';
} else {
  primaryPlatform = 'youtube';
}

return [{
  json: {
    ...originalData,
    socialMedia: {
      instagram,
      tiktok,
      youtube,
      totalFollowers,
      primaryPlatform
    }
  },
  pairedItem: { item: 0 }
}];

// Process Amazon Q&A data from Oxylabs API
const items = $input.all();
const originalData = $('Run Q&A Analysis?').first().json;
const response = items[0]?.json || {};

// Check for API errors
if (response.error || response.status === 'error' || !response.results) {
  const errorMsg = response.error?.message || response.message || 'Oxylabs API error';
  return [{
    json: {
      ...originalData,
      qaAnalysis: {
        error: errorMsg,
        totalQuestions: 0,
        answeredQuestions: 0,
        unansweredQuestions: 0,
        avgAnswersPerQuestion: 0,
        topQuestions: [],
        commonThemes: []
      },
      questions: []
    },
    pairedItem: { item: 0 }
  }];
}

// Parse Oxylabs response - results[0].content contains parsed data
const content = response.results?.[0]?.content || {};
let questions = content.questions || content.qa || [];

// If questions is not an array, try to extract from different response structures
if (!Array.isArray(questions)) {
  questions = [];
}

const qaAnalysis = {
  totalQuestions: questions.length,
  answeredQuestions: questions.filter(q => q.answer || q.answers?.length > 0).length,
  unansweredQuestions: questions.filter(q => !q.answer && (!q.answers || q.answers.length === 0)).length,
  avgAnswersPerQuestion: questions.length > 0 
    ? questions.reduce((sum, q) => sum + (q.answer_count || q.answers?.length || (q.answer ? 1 : 0)), 0) / questions.length 
    : 0,
  topQuestions: questions.slice(0, 10).map(q => ({
    question: q.question || q.text,
    answer: q.answer || q.top_answer || q.answers?.[0]?.text || q.answers?.[0] || null,
    votes: q.votes || q.helpful_votes || 0,
    answerCount: q.answer_count || q.answers?.length || (q.answer ? 1 : 0),
    askedBy: q.author || q.asked_by || null,
    answeredBy: q.answered_by || null
  })),
  commonThemes: extractThemes(questions),
  source: 'oxylabs'
};

function extractThemes(questions) {
  if (questions.length === 0) return [];
  const themes = {};
  const keywords = ['size', 'fit', 'quality', 'battery', 'warranty', 'return', 'shipping', 
                    'compatible', 'work with', 'how to', 'difference', 'vs', 'better',
                    'color', 'material', 'weight', 'install', 'use', 'last'];

  questions.forEach(q => {
    const text = (q.question || q.text || '').toLowerCase();
    keywords.forEach(kw => {
      if (text.includes(kw)) {
        themes[kw] = (themes[kw] || 0) + 1;
      }
    });
  });

  return Object.entries(themes)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5)
    .map(([theme, count]) => ({ theme, count }));
}

return [{
  json: {
    ...originalData,
    qaAnalysis,
    questions: questions.slice(0, 20),
    qaSource: 'oxylabs'
  },
  pairedItem: { item: 0 }
}];

// Process advertising data from Facebook Ad Library and Google Ads
const items = $input.all();
const originalData = $('Run Ad Analysis?').first().json;

// Safely extract ad data, handling error responses
let fbAds = [];
let googleAds = [];

try {
  const fbResponse = items[0]?.json;
  // Check if response is valid JSON data (not HTML error)
  if (fbResponse && typeof fbResponse === 'object' && !fbResponse.data?.includes?.('<!DOCTYPE')) {
    fbAds = fbResponse.data || fbResponse.ads || [];
    if (!Array.isArray(fbAds)) fbAds = [];
  }
} catch (e) {
  fbAds = [];
}

try {
  const googleResponse = items[1]?.json;
  if (googleResponse && typeof googleResponse === 'object' && !googleResponse.data?.includes?.('<!DOCTYPE')) {
    googleAds = googleResponse.data || googleResponse.advertisers || [];
    if (!Array.isArray(googleAds)) googleAds = [];
  }
} catch (e) {
  googleAds = [];
}

function countAdTypes(ads) {
  if (!Array.isArray(ads)) return {};
  return ads.reduce((acc, ad) => {
    const type = ad?.ad_type || ad?.media_type || 'other';
    acc[type] = (acc[type] || 0) + 1;
    return acc;
  }, {});
}

function countPlatforms(ads) {
  if (!Array.isArray(ads)) return {};
  return ads.reduce((acc, ad) => {
    (ad?.publisher_platforms || []).forEach(p => {
      acc[p] = (acc[p] || 0) + 1;
    });
    return acc;
  }, {});
}

// Process Facebook ads
const fbAnalysis = {
  activeAds: fbAds.filter(ad => ad?.is_active || ad?.status === 'active').length,
  totalAds: fbAds.length,
  adTypes: countAdTypes(fbAds),
  platforms: countPlatforms(fbAds),
  topCreatives: fbAds.slice(0, 5).map(ad => ({
    id: ad?.id || ad?.ad_id,
    type: ad?.ad_type || ad?.media_type || 'unknown',
    startDate: ad?.start_date || ad?.ad_delivery_start_time,
    creative: ad?.ad_creative_link_captions?.[0] || ad?.body_text || null,
    landingPage: ad?.ad_creative_link_destinations?.[0] || ad?.link_url || null,
    imageUrl: ad?.ad_snapshot_url || ad?.image_url || null
  }))
};

// Process Google ads
const googleAnalysis = {
  advertisersFound: googleAds.length,
  adFormats: googleAds.reduce((acc, adv) => {
    (adv?.ad_formats || []).forEach(f => {
      acc[f] = (acc[f] || 0) + 1;
    });
    return acc;
  }, {}),
  topAdvertisers: googleAds.slice(0, 3).map(adv => ({
    name: adv?.advertiser_name || adv?.name,
    id: adv?.advertiser_id || adv?.id,
    verificationStatus: adv?.verification_status || null
  }))
};

// Estimate ad spend based on number of active ads
let spendEstimate = 'unknown';
if (fbAnalysis.activeAds > 50) spendEstimate = 'high ($10k+/month)';
else if (fbAnalysis.activeAds > 20) spendEstimate = 'medium ($2k-$10k/month)';
else if (fbAnalysis.activeAds > 5) spendEstimate = 'low ($500-$2k/month)';
else if (fbAnalysis.activeAds > 0) spendEstimate = 'minimal (<$500/month)';
else spendEstimate = 'none detected';

return [{
  json: {
    ...originalData,
    adAnalysis: {
      facebook: fbAnalysis,
      google: googleAnalysis,
      spendEstimate,
      scrapedAt: new Date().toISOString()
    }
  }
}];

