if (!data.products || !data.products[0]) {
  return [{ json: { error: 'No product found', ...setConfigOutput } }];
}

const p = data.products[0];

// Helper functions - return NUMBERS not strings
const getPrice = (csv, i) => {
  if (!csv?.[i]?.length || csv[i].length < 2) return null;
  const val = csv[i][csv[i].length-1];
  return val > 0 ? val / 100 : null;
};

const getPriceHistory = (csv, i, days = 30) => {
  if (!csv?.[i]?.length) return null;
  const now = Date.now();
  const cutoff = now - (days * 24 * 60 * 60 * 1000);
  const history = [];
  for (let j = 0; j < csv[i].length; j += 2) {
    const timestamp = (csv[i][j] + 21564000) * 60000;
    if (timestamp >= cutoff && csv[i][j+1] > 0) {
      history.push({ date: new Date(timestamp).toISOString().split('T')[0], value: csv[i][j+1] / 100 });
    }
  }
  return history.length > 0 ? history : null;
};

const getBSR = (sr) => {
  if (!sr) return null;
  const categories = Object.keys(sr);
  if (!categories.length) return null;
  const mainCat = categories[0];
  const ranks = sr[mainCat];
  return ranks?.length ? ranks[ranks.length - 1] : null;
};

const getAllBSR = (sr) => {
  if (!sr) return {};
  const result = {};
  for (const [catId, ranks] of Object.entries(sr)) {
    if (ranks?.length) {
      result[catId] = {
        current: ranks[ranks.length - 1],
        history: ranks.slice(-10)
      };
    }
  }
  return result;
};

const calcBSRChange = (sr, days = 30) => {
  if (!sr) return null;
  const mainCat = Object.keys(sr)[0];
  if (!mainCat || !sr[mainCat]?.length) return null;
  const ranks = sr[mainCat];
  const current = ranks[ranks.length - 1];
  const older = ranks.length > 2 ? ranks[Math.max(0, ranks.length - (days * 2))] : ranks[0];
  if (!older || !current) return null;
  return current - older;
};

const calcBSRGrowth = (sr, days = 30) => {
  if (!sr) return null;
  const mainCat = Object.keys(sr)[0];
  if (!mainCat || !sr[mainCat]?.length) return null;
  const ranks = sr[mainCat];
  const current = ranks[ranks.length - 1];
  const older = ranks.length > 2 ? ranks[Math.max(0, ranks.length - (days * 2))] : ranks[0];
  if (!older || !current) return null;
  return ((older - current) / older * 100);
};

const calcSalesGrowth = (history) => {
  if (!history || history.length < 2) return null;
  const current = history[history.length - 1];
  const previous = history[Math.max(0, history.length - 2)];
  if (!previous || previous === 0) return null;
  return ((current - previous) / previous * 100);
};

const calcNewRatings = (csv, days = 30) => {
  if (!csv?.[17]?.length || csv[17].length < 4) return null;
  const current = csv[17][csv[17].length - 1];
  const older = csv[17][Math.max(0, csv[17].length - (days * 2) - 1)];
  if (older === undefined) return null;
  return current - older;
};

const mmToInches = (mm) => mm && mm > 0 ? mm / 25.4 : null;
const mmToCm = (mm) => mm && mm > 0 ? mm / 10 : null;
const gramsToOz = (g) => g && g > 0 ? g / 28.3495 : null;
const gramsToLbs = (g) => g && g > 0 ? g / 453.592 : null;
const gramsToKg = (g) => g && g > 0 ? g / 1000 : null;
const keepaTimeToDate = (kt) => kt ? new Date((kt + 21564000) * 60000).toISOString().split('T')[0] : null;

const getSubCategories = (sr, rootCat) => {
  if (!sr) return [];
  const subCats = [];
  for (const [catId, ranks] of Object.entries(sr)) {
    if (catId !== String(rootCat) && ranks?.length) {
      subCats.push({
        categoryId: parseInt(catId),
        currentBSR: ranks[ranks.length - 1],
        bsrHistory: ranks.slice(-10)
      });
    }
  }
  return subCats;
};

const getFulfillmentType = (offers) => {
  if (!offers || !offers.length) return null;
  const fbaCount = offers.filter(o => o.isFBA).length;
  const fbmCount = offers.length - fbaCount;
  if (fbaCount > fbmCount) return 'FBA';
  if (fbmCount > fbaCount) return 'FBM';
  return 'Mixed';
};

const getSizeTierLimits = (tier) => {
  const limits = {
